sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  if(inputs.status_code =="200"){

  var responsebody = JSON.parse(inputs.response_body);

    outputs.latitude = responsebody.results[0].bounds.northeast.lat;
     outputs.longitude = responsebody.results[0].bounds.northeast.lng ;
     outputs.city = responsebody.results[0].components.city ;

  }


})(inputs, outputs);

